let burgerBtn=document.querySelector(".burger-btn"),menu=document.querySelector(".burger-menu__layer");burgerBtn.addEventListener("click",()=>{burgerBtn.classList.toggle("active"),menu.classList.toggle("active")}),document.addEventListener("click",e=>{e.target.closest(".burger-menu")||(burgerBtn.classList.remove("active"),menu.classList.remove("active"))}),document.addEventListener("DOMContentLoaded",function(){let t=window.location.pathname;document.querySelectorAll("nav a").forEach(e=>{e.getAttribute("href")===t&&e.classList.add("menu__link_active")})});